mapper.select:

WHERE true

ORDER BY reg_date DESC, bno DESC
LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE board
SET   title = #{title}
  , content = #{content}
  , up_date = now()
WHERE bno = #{bno} and writer = #{writer};

UPDATE board
SET   comment_cnt = comment_cnt + #{cnt}
WHERE bno = #{bno}

UPDATE board
SET   view_cnt = view_cnt + 1
WHERE bno = #{bno}
